import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { FaUser, FaEnvelope, FaLock, FaEdit, FaSave, FaTimes, FaPhone, FaMapMarkerAlt, FaCity, FaMailBulk, FaGlobe } from 'react-icons/fa';
import '../../styles/user/UserProfile.css';

const UserProfile = () => {
  const { currentUser, token } = useContext(AuthContext);
  const [profileData, setProfileData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    postalCode: '',
    country: 'USA'
  });
  const [editMode, setEditMode] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [changePassword, setChangePassword] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  useEffect(() => {
    // Initialize with current user data or fetch from API
    if (currentUser) {
      setProfileData(prevData => ({
        ...prevData,
        name: currentUser.name || '',
        email: currentUser.email || ''
      }));
    }
    
    // Fetch additional user profile data if available
    const fetchUserProfile = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://localhost:5000/api/users/profile', {
          headers: {
            'x-auth-token': token
          }
        });
        
        if (response.ok) {
          const data = await response.json();
          setProfileData(prevData => ({
            ...prevData,
            phone: data.phone || '',
            address: data.address || '',
            city: data.city || '',
            postalCode: data.postalCode || '',
            country: data.country || 'USA'
          }));
        }
      } catch (err) {
        console.log('Profile data not fully available yet');
        // Not setting error here as this is optional additional data
      } finally {
        setLoading(false);
      }
    };

    if (token) {
      fetchUserProfile();
    }
  }, [currentUser, token]);

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const toggleEditMode = () => {
    setEditMode(!editMode);
    setError('');
    setSuccess('');
  };

  const toggleChangePassword = () => {
    setChangePassword(!changePassword);
    setPasswordData({
      currentPassword: '',
      newPassword: '',
      confirmPassword: ''
    });
    setError('');
    setSuccess('');
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      // In a real app, this would be an API call to update user profile
      const response = await fetch('http://localhost:5000/api/users/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': token
        },
        body: JSON.stringify(profileData)
      });

      if (response.ok) {
        setSuccess('Profile updated successfully!');
        setEditMode(false);
      } else {
        const data = await response.json();
        throw new Error(data.message || 'Failed to update profile');
      }
    } catch (err) {
      setError(err.message || 'An error occurred while updating profile');
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validate passwords
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      setError('New passwords do not match');
      return;
    }

    if (passwordData.newPassword.length < 6) {
      setError('Password must be at least 6 characters long');
      return;
    }

    setLoading(true);

    try {
      // In a real app, this would be an API call to change password
      const response = await fetch('http://localhost:5000/api/users/change-password', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': token
        },
        body: JSON.stringify({
          currentPassword: passwordData.currentPassword,
          newPassword: passwordData.newPassword
        })
      });

      if (response.ok) {
        setSuccess('Password changed successfully!');
        setChangePassword(false);
        setPasswordData({
          currentPassword: '',
          newPassword: '',
          confirmPassword: ''
        });
      } else {
        const data = await response.json();
        throw new Error(data.message || 'Failed to change password');
      }
    } catch (err) {
      setError(err.message || 'An error occurred while changing password');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="user-profile-container">
      <div className="profile-header">
        <h2>My Profile</h2>
        {!editMode && !changePassword && (
          <button className="edit-profile-btn" onClick={toggleEditMode}>
            <FaEdit /> Edit Profile
          </button>
        )}
      </div>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      {/* Profile Information Section */}
      <div className="profile-section">
        {!changePassword && (
          <>
            <h3>Personal Information</h3>
            {editMode ? (
              <form onSubmit={handleProfileSubmit}>
                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="name">
                      <FaUser /> Full Name
                    </label>
                    <input
                      type="text"
                      id="name"
                      name="name"
                      value={profileData.name}
                      onChange={handleProfileChange}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="email">
                      <FaEnvelope /> Email Address
                    </label>
                    <input
                      type="email"
                      id="email"
                      name="email"
                      value={profileData.email}
                      onChange={handleProfileChange}
                      required
                      readOnly // Email typically shouldn't be changed
                      className="readonly"
                    />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="phone">
                      <FaPhone /> Phone Number
                    </label>
                    <input
                      type="tel"
                      id="phone"
                      name="phone"
                      value={profileData.phone}
                      onChange={handleProfileChange}
                    />
                  </div>
                </div>

                <h3>Shipping Address</h3>
                <div className="form-group">
                  <label htmlFor="address">
                    <FaMapMarkerAlt /> Address
                  </label>
                  <input
                    type="text"
                    id="address"
                    name="address"
                    value={profileData.address}
                    onChange={handleProfileChange}
                  />
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="city">
                      <FaCity /> City
                    </label>
                    <input
                      type="text"
                      id="city"
                      name="city"
                      value={profileData.city}
                      onChange={handleProfileChange}
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="postalCode">
                      <FaMailBulk /> Postal Code
                    </label>
                    <input
                      type="text"
                      id="postalCode"
                      name="postalCode"
                      value={profileData.postalCode}
                      onChange={handleProfileChange}
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="country">
                    <FaGlobe /> Country
                  </label>
                  <select
                    id="country"
                    name="country"
                    value={profileData.country}
                    onChange={handleProfileChange}
                  >
                    <option value="USA">United States</option>
                    <option value="Canada">Canada</option>
                    <option value="UK">United Kingdom</option>
                    <option value="Australia">Australia</option>
                    {/* Add more countries as needed */}
                  </select>
                </div>

                <div className="form-actions">
                  <button type="submit" className="save-btn" disabled={loading}>
                    {loading ? 'Saving...' : <><FaSave /> Save Changes</>}
                  </button>
                  <button type="button" className="cancel-btn" onClick={toggleEditMode}>
                    <FaTimes /> Cancel
                  </button>
                </div>
              </form>
            ) : (
              <div className="profile-info">
                <div className="info-row">
                  <div className="info-group">
                    <span className="info-label"><FaUser /> Name</span>
                    <span className="info-value">{profileData.name}</span>
                  </div>
                  <div className="info-group">
                    <span className="info-label"><FaEnvelope /> Email</span>
                    <span className="info-value">{profileData.email}</span>
                  </div>
                </div>

                <div className="info-row">
                  <div className="info-group">
                    <span className="info-label"><FaPhone /> Phone</span>
                    <span className="info-value">
                      {profileData.phone || 'Not provided'}
                    </span>
                  </div>
                </div>

                <h3>Shipping Address</h3>
                <div className="info-group">
                  <span className="info-label"><FaMapMarkerAlt /> Address</span>
                  <span className="info-value">
                    {profileData.address || 'Not provided'}
                  </span>
                </div>

                <div className="info-row">
                  <div className="info-group">
                    <span className="info-label"><FaCity /> City</span>
                    <span className="info-value">
                      {profileData.city || 'Not provided'}
                    </span>
                  </div>
                  <div className="info-group">
                    <span className="info-label"><FaMailBulk /> Postal Code</span>
                    <span className="info-value">
                      {profileData.postalCode || 'Not provided'}
                    </span>
                  </div>
                </div>

                <div className="info-group">
                  <span className="info-label"><FaGlobe /> Country</span>
                  <span className="info-value">{profileData.country}</span>
                </div>
              </div>
            )}

            {/* Security Section */}
            {!editMode && (
              <div className="security-section">
                <h3>Security</h3>
                <button className="change-password-btn" onClick={toggleChangePassword}>
                  <FaLock /> Change Password
                </button>
              </div>
            )}
          </>
        )}

        {/* Password Change Form */}
        {changePassword && (
          <div className="password-section">
            <h3>Change Password</h3>
            <form onSubmit={handlePasswordSubmit}>
              <div className="form-group">
                <label htmlFor="currentPassword">Current Password</label>
                <input
                  type="password"
                  id="currentPassword"
                  name="currentPassword"
                  value={passwordData.currentPassword}
                  onChange={handlePasswordChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="newPassword">New Password</label>
                <input
                  type="password"
                  id="newPassword"
                  name="newPassword"
                  value={passwordData.newPassword}
                  onChange={handlePasswordChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="confirmPassword">Confirm New Password</label>
                <input
                  type="password"
                  id="confirmPassword"
                  name="confirmPassword"
                  value={passwordData.confirmPassword}
                  onChange={handlePasswordChange}
                  required
                />
              </div>

              <div className="password-requirements">
                <p>Password must be at least 6 characters long</p>
              </div>

              <div className="form-actions">
                <button type="submit" className="save-btn" disabled={loading}>
                  {loading ? 'Updating...' : 'Update Password'}
                </button>
                <button type="button" className="cancel-btn" onClick={toggleChangePassword}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
