import React, { useState, useEffect, useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { FaBox, FaArrowLeft } from 'react-icons/fa';
import '../styles/Orders.css';

const Orders = () => {
  const { token, currentUser } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentDateTime] = useState('2025-03-27 08:28:17');
  const [currentUserName] = useState('Krizzna69');
  const location = useLocation();
  
  // Check if a new order was just placed
  const newOrderId = location.state?.newOrderId;
  
  useEffect(() => {
    fetchOrders();
    // Debug current state
    console.log("Orders component mounted, AuthContext:", currentUser);
  }, [token, newOrderId, currentUser]); 
  
  const fetchOrders = async () => {
    setLoading(true);
    
    try {
      // Get user ID from context or use username as fallback
      const userId = currentUser?.id || currentUserName || 'anonymous';
      console.log('Fetching orders for user:', userId);
      
      // Construct user-specific storage key
      const userOrdersKey = `orders_${userId}`;
      
      // Get orders for this specific user
      let userOrders = [];
      
      try {
        const userOrdersStr = localStorage.getItem(userOrdersKey);
        if (userOrdersStr) {
          const parsedOrders = JSON.parse(userOrdersStr);
          console.log('Raw orders data from localStorage:', userOrdersStr.substring(0, 100) + '...');
          
          if (Array.isArray(parsedOrders)) {
            userOrders = parsedOrders;
            console.log('Found orders for user:', userId, userOrders.length);
            // Debug first order if exists
            if (userOrders.length > 0) {
              console.log('First order:', JSON.stringify(userOrders[0]).substring(0, 100) + '...');
            }
          }
        }
      } catch (err) {
        console.error("Error parsing user orders from localStorage:", err);
      }
      
      // If still no orders, check old format for this user (for backward compatibility)
      if (userOrders.length === 0) {
        console.log('Checking allOrders for user orders');
        try {
          const allOrdersStr = localStorage.getItem('allOrders');
          if (allOrdersStr) {
            const allOrders = JSON.parse(allOrdersStr);
            if (Array.isArray(allOrders)) {
              // Filter to only this user's orders
              userOrders = allOrders.filter(order => 
                order.userId === userId || 
                order.userName === currentUserName
              );
              console.log('Found orders in allOrders for user:', userOrders.length);
            }
          }
        } catch (err) {
          console.error("Error parsing all orders:", err);
        }
      }
      
      // Last fallback - check lastOrder
      if (userOrders.length === 0) {
        console.log('Checking lastOrder as final fallback');
        try {
          const lastOrderStr = localStorage.getItem('lastOrder');
          if (lastOrderStr) {
            const lastOrder = JSON.parse(lastOrderStr);
            if (lastOrder && lastOrder._id) {
              // Only include if it belongs to this user or has no user ID
              if (!lastOrder.userId || lastOrder.userId === userId || 
                  lastOrder.userName === currentUserName) {
                userOrders = [lastOrder];
                console.log('Found order in lastOrder:', lastOrder._id);
              }
            }
          }
        } catch (err) {
          console.error("Error parsing last order:", err);
        }
      }
      
      console.log(`Setting ${userOrders.length} orders in state`);
      // Set the orders in state
      setOrders(userOrders);
    } catch (err) {
      console.error("Error fetching orders:", err);
      setError('Failed to load orders. Please try again later.');
    } finally {
      setLoading(false);
    }
  };
  
  const formatDate = (dateString) => {
    try {
      const options = { 
        year: 'numeric', 
        month: 'long', 
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      };
      return new Date(dateString).toLocaleDateString(undefined, options);
    } catch (err) {
      return 'Unknown date';
    }
  };
  
  const formatPrice = (price) => {
    // Add null check before calling toFixed
    return typeof price === 'number' ? `$${price.toFixed(2)}` : '$0.00';
  };
  
  // Calculate item total safely
  const calculateItemTotal = (item) => {
    if (!item) return 0;
    const price = typeof item.price === 'number' ? item.price : 0;
    const quantity = typeof item.quantity === 'number' ? item.quantity : 0;
    return price * quantity;
  };
  
  // Calculate order subtotal safely
  const calculateOrderSubtotal = (items) => {
    if (!Array.isArray(items)) return 0;
    return items.reduce((sum, item) => sum + calculateItemTotal(item), 0);
  };
  
  if (loading) {
    return <div className="loading">Loading your orders...</div>;
  }
  
  if (error) {
    return <div className="error-message">{error}</div>;
  }
  
  console.log(`Rendering orders list with ${orders.length} orders`);
  
  if (!orders || orders.length === 0) {
    return (
      <div className="no-orders">
        <FaBox className="no-orders-icon" />
        <h2>No Orders Found</h2>
        <p>You haven't placed any orders yet.</p>
        <Link to="/user" className="shop-now-btn">
          <FaArrowLeft /> Shop Now
        </Link>
      </div>
    );
  }
  
  return (
    <div className="orders-page">
      <div className="orders-header">
        <h1>My Orders</h1>
        <div className="orders-user-info">
          <span>{currentDateTime}</span>
          <span>User: {currentUserName}</span>
        </div>
      </div>
      
      <div className="orders-list">
        {orders.map((order, index) => {
          // Skip invalid orders
          if (!order || !order._id) {
            console.log(`Skipping invalid order at index ${index}`);
            return null;
          }
          
          console.log(`Rendering order ${index}:`, order._id);
          
          const orderSubtotal = calculateOrderSubtotal(order.items);
          const shippingCost = (typeof order.totalAmount === 'number' && order.totalAmount > orderSubtotal) 
            ? order.totalAmount - orderSubtotal 
            : 0;
          
          return (
            <div 
              key={order._id} 
              className={`order-card ${newOrderId === order._id ? 'new-order' : ''}`}
              id={newOrderId === order._id ? 'new-order' : undefined}
              style={{ display: 'block', visibility: 'visible' }} // Force display
            >
              {newOrderId === order._id && (
                <div className="new-order-badge">New Order</div>
              )}
              
              <div className="order-header">
                <div className="order-info">
                  <h3>Order #{order._id}</h3>
                  <p className="order-date">
                    Placed on {order.createdAt ? formatDate(order.createdAt) : 'Unknown date'}
                  </p>
                </div>
                <div className="order-status">
                  <span className={`status-badge ${(order.status || '').toLowerCase()}`}>
                    {order.status || 'Processing'}
                  </span>
                  <span className="order-total">{formatPrice(order.totalAmount)}</span>
                </div>
              </div>
              
              <div className="order-items">
                {Array.isArray(order.items) ? order.items.map((item, itemIndex) => {
                  if (!item || !item.product) {
                    console.log(`Skipping invalid item at index ${itemIndex} in order ${order._id}`);
                    return null;
                  }
                  
                  return (
                    <div key={itemIndex} className="order-item">
                      <div className="item-image">
                        <img 
                          src={item.product.imageUrl || 'https://via.placeholder.com/60'} 
                          alt={item.product.name || 'Product'} 
                        />
                      </div>
                      <div className="item-details">
                        <h4>{item.product.name || 'Unknown Product'}</h4>
                        <p className="item-price">
                          {formatPrice(item.price)} x {item.quantity || 1}
                        </p>
                      </div>
                      <div className="item-total">
                        {formatPrice(calculateItemTotal(item))}
                      </div>
                    </div>
                  );
                }) : (
                  <div className="no-items">No items available</div>
                )}
              </div>
              
              <div className="order-footer">
                {order.shippingAddress && (
                  <div className="shipping-info">
                    <h4>Shipping Address</h4>
                    <p>{order.shippingAddress.fullName || 'N/A'}</p>
                    <p>{order.shippingAddress.address || 'N/A'}</p>
                    <p>
                      {order.shippingAddress.city || 'N/A'}, 
                      {' '}{order.shippingAddress.state || 'N/A'} 
                      {' '}{order.shippingAddress.zipCode || 'N/A'}
                    </p>
                    {order.shippingAddress.phoneNumber && (
                      <p>Phone: {order.shippingAddress.phoneNumber}</p>
                    )}
                  </div>
                )}
                
                <div className="payment-info">
                  <h4>Payment Method</h4>
                  <p>{order.paymentMethod === 'COD' ? 'Cash on Delivery' : (order.paymentMethod || 'N/A')}</p>
                  
                  <h4 className="order-summary-title">Order Summary</h4>
                  <div className="order-summary">
                    <div className="summary-row">
                      <span>Items Total:</span>
                      <span>{formatPrice(orderSubtotal)}</span>
                    </div>
                    <div className="summary-row">
                      <span>Shipping:</span>
                      <span>{shippingCost > 0 ? formatPrice(shippingCost) : 'Free'}</span>
                    </div>
                    <div className="summary-divider"></div>
                    <div className="summary-row total">
                      <span>Total:</span>
                      <span>{formatPrice(order.totalAmount)}</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Orders;
