import React, { useState, useEffect, useContext } from 'react';
import { Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import AddProduct from '../components/AddProduct';
import '../styles/AdminDashboard.css';

// Dashboard components
import DashboardHome from '../components/admin/DashboardHome';
import ProductManagement from '../components/admin/ProductManagement';
import OrderManagement from '../components/admin/OrderManagement';
import UserManagement from '../components/admin/UserManagement';
import Settings from '../components/admin/Settings';

// Icons
import { FaHome, FaBoxOpen, FaShoppingCart, FaUsers, FaCog, FaBars, FaTimes, FaSignOutAlt, FaUser } from 'react-icons/fa';

const AdminDashboard = () => {
  const { currentUser, logout } = useContext(AuthContext);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [currentDateTime, setCurrentDateTime] = useState('2025-03-26 15:02:09');
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Update date time every minute
    const interval = setInterval(() => {
      const now = new Date();
      const formatted = now.getUTCFullYear() + '-' + 
                        String(now.getUTCMonth() + 1).padStart(2, '0') + '-' + 
                        String(now.getUTCDate()).padStart(2, '0') + ' ' + 
                        String(now.getUTCHours()).padStart(2, '0') + ':' + 
                        String(now.getUTCMinutes()).padStart(2, '0') + ':' + 
                        String(now.getUTCSeconds()).padStart(2, '0');
      setCurrentDateTime(formatted);
    }, 60000);

    // Handle window resize for mobile view
    const handleResize = () => {
      const mobile = window.innerWidth < 768;
      setIsMobile(mobile);
      if (mobile) setSidebarOpen(false);
    };

    window.addEventListener('resize', handleResize);
    
    return () => {
      clearInterval(interval);
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const isActive = (path) => {
    return location.pathname === path || location.pathname.startsWith(path + '/');
  };

  return (
    <div className="admin-dashboard">
      {/* Mobile Menu Toggle */}
      <button className="mobile-menu-toggle" onClick={toggleSidebar}>
        {sidebarOpen ? <FaTimes /> : <FaBars />}
      </button>

      {/* Sidebar */}
      <div className={`admin-sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="admin-sidebar-header">
          <h2>Grocery Shop</h2>
          <span className="admin-sidebar-role">ADMIN PANEL</span>
        </div>

        <div className="admin-sidebar-user">
          <div className="avatar-circle">
            <FaUser />
          </div>
          <div className="user-info">
            <h3>{currentUser?.name || 'Krizzna69'}</h3>
            <span>Administrator</span>
          </div>
        </div>

        <nav className="admin-sidebar-nav">
          <ul>
            <li className={isActive('/admin') ? 'active' : ''}>
              <Link to="/admin" onClick={() => isMobile && setSidebarOpen(false)}>
                <FaHome /> <span>Dashboard</span>
              </Link>
            </li>
            <li className={isActive('/admin/products') ? 'active' : ''}>
              <Link to="/admin/products" onClick={() => isMobile && setSidebarOpen(false)}>
                <FaBoxOpen /> <span>Products</span>
              </Link>
            </li>
            <li className={isActive('/admin/orders') ? 'active' : ''}>
              <Link to="/admin/orders" onClick={() => isMobile && setSidebarOpen(false)}>
                <FaShoppingCart /> <span>Orders</span>
              </Link>
            </li>
            <li className={isActive('/admin/users') ? 'active' : ''}>
              <Link to="/admin/users" onClick={() => isMobile && setSidebarOpen(false)}>
                <FaUsers /> <span>Users</span>
              </Link>
            </li>
            <li className={isActive('/admin/settings') ? 'active' : ''}>
              <Link to="/admin/settings" onClick={() => isMobile && setSidebarOpen(false)}>
                <FaCog /> <span>Settings</span>
              </Link>
            </li>
          </ul>
        </nav>
        
        <div className="admin-sidebar-footer">
          <button onClick={handleLogout} className="logout-button">
            <FaSignOutAlt /> <span>Logout</span>
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="admin-content">
        {/* Top Bar */}
        <div className="admin-topbar">
          <div className="admin-breadcrumb">
            <h1>
              {location.pathname === '/admin' && 'Dashboard Overview'}
              {location.pathname === '/admin/products' && 'Product Management'}
              {location.pathname === '/admin/add-product' && 'Add New Product'}
              {location.pathname === '/admin/orders' && 'Order Management'}
              {location.pathname === '/admin/users' && 'User Management'}
              {location.pathname === '/admin/settings' && 'Settings'}
            </h1>
          </div>
          <div className="admin-topbar-right">
            <div className="admin-datetime">{currentDateTime}</div>
          </div>
        </div>

        {/* Dashboard Content */}
        <div className="admin-main-content">
          <Routes>
            <Route path="/" element={<DashboardHome />} />
            <Route path="/products" element={<ProductManagement />} />
            <Route path="/add-product" element={<AddProduct />} />
            <Route path="/orders" element={<OrderManagement />} />
            <Route path="/users" element={<UserManagement />} />
            <Route path="/settings" element={<Settings />} />
          </Routes>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
