import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../../context/CartContext';
import { AuthContext } from '../../context/AuthContext';
import { FaTrash, FaMinus, FaPlus, FaShoppingCart } from 'react-icons/fa';
import '../../styles/user/ShoppingCart.css';

const ShoppingCart = () => {
  const { cartItems, cartTotal, removeFromCart, updateQuantity, clearCart } = useContext(CartContext);
  const { token, currentUser } = useContext(AuthContext);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [shippingAddress, setShippingAddress] = useState({
    address: '',
    city: '',
    postalCode: '',
    country: 'USA'
  });
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [checkoutStep, setCheckoutStep] = useState(1);
  const navigate = useNavigate();

  const handleQuantityChange = (productId, newQuantity) => {
    if (newQuantity >= 1) {
      updateQuantity(productId, newQuantity);
    }
  };

  const handleRemoveItem = (productId) => {
    removeFromCart(productId);
  };

  const handleShippingInputChange = (e) => {
    const { name, value } = e.target;
    setShippingAddress(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePaymentMethodChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  const isAddressComplete = () => {
    return (
      shippingAddress.address.trim() !== '' &&
      shippingAddress.city.trim() !== '' &&
      shippingAddress.postalCode.trim() !== '' &&
      shippingAddress.country.trim() !== ''
    );
  };

  const proceedToShipping = () => {
    if (cartItems.length > 0) {
      setCheckoutStep(2);
    }
  };

  const proceedToPayment = () => {
    if (isAddressComplete()) {
      setCheckoutStep(3);
    }
  };

  const handleCheckout = async () => {
    if (cartItems.length === 0) return;

    setLoading(true);
    setError('');

    try {
      // Prepare order data
      const orderData = {
        products: cartItems.map(item => ({
          product: item._id,
          name: item.name,
          quantity: item.quantity,
          price: item.price
        })),
        shippingAddress,
        paymentMethod,
        totalAmount: cartTotal
      };

      // Make API call to create order - uncomment when backend is ready
      /*
      const response = await fetch('http://localhost:5000/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': token
        },
        body: JSON.stringify(orderData)
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to create order');
      }
      */

      // For now, simulate a successful order
      setTimeout(() => {
        // Clear the cart
        clearCart();
        
        // Show success and redirect
        alert('Order placed successfully!');
        navigate('/user/orders');
        
        setLoading(false);
      }, 1500);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  const renderCartItems = () => {
    if (cartItems.length === 0) {
      return (
        <div className="empty-cart">
          <div className="empty-cart-icon">
            <FaShoppingCart />
          </div>
          <h3>Your cart is empty</h3>
          <p>Looks like you haven't added any items to your cart yet.</p>
          <button 
            className="continue-shopping-btn"
            onClick={() => navigate('/user')}
          >
            Continue Shopping
          </button>
        </div>
      );
    }

    return (
      <div className="cart-items">
        <table className="cart-table">
          <thead>
            <tr>
              <th>Product</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Subtotal</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map(item => (
              <tr key={item._id}>
                <td className="cart-product">
                  <div className="cart-product-image">
                    {item.imageUrl ? (
                      <img src={item.imageUrl} alt={item.name} />
                    ) : (
                      <div className="no-image">No Image</div>
                    )}
                  </div>
                  <div className="cart-product-info">
                    <h4>{item.name}</h4>
                    <span>{item.category}</span>
                  </div>
                </td>
                <td className="cart-price">${item.price.toFixed(2)}</td>
                <td className="cart-quantity">
                  <div className="quantity-control">
                    <button 
                      onClick={() => handleQuantityChange(item._id, item.quantity - 1)}
                      disabled={item.quantity <= 1}
                    >
                      <FaMinus />
                    </button>
                    <span>{item.quantity}</span>
                    <button onClick={() => handleQuantityChange(item._id, item.quantity + 1)}>
                      <FaPlus />
                    </button>
                  </div>
                </td>
                <td className="cart-subtotal">
                  ${(item.price * item.quantity).toFixed(2)}
                </td>
                <td className="cart-actions">
                  <button 
                    className="remove-item-btn"
                    onClick={() => handleRemoveItem(item._id)}
                  >
                    <FaTrash />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  const renderCartSummary = () => {
    return (
      <div className="cart-summary">
        <h3>Order Summary</h3>
        <div className="summary-row">
          <span>Subtotal ({cartItems.reduce((sum, item) => sum + item.quantity, 0)} items)</span>
          <span>${cartTotal.toFixed(2)}</span>
        </div>
        <div className="summary-row">
          <span>Shipping Fee</span>
          <span>$0.00</span>
        </div>
        <div className="summary-row total">
          <span>Total</span>
          <span>${cartTotal.toFixed(2)}</span>
        </div>
        {checkoutStep === 1 && (
          <button 
            className="checkout-btn"
            onClick={proceedToShipping}
            disabled={cartItems.length === 0}
          >
            Proceed to Checkout
          </button>
        )}
      </div>
    );
  };

  const renderShippingForm = () => {
    return (
      <div className="shipping-form">
        <h3>Shipping Information</h3>
        
        <div className="form-group">
          <label htmlFor="address">Street Address</label>
          <input
            type="text"
            id="address"
            name="address"
            value={shippingAddress.address}
            onChange={handleShippingInputChange}
            required
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="city">City</label>
          <input
            type="text"
            id="city"
            name="city"
            value={shippingAddress.city}
            onChange={handleShippingInputChange}
            required
          />
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="postalCode">Postal Code</label>
            <input
              type="text"
              id="postalCode"
              name="postalCode"
              value={shippingAddress.postalCode}
              onChange={handleShippingInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="country">Country</label>
            <select
              id="country"
              name="country"
              value={shippingAddress.country}
              onChange={handleShippingInputChange}
              required
            >
              <option value="USA">United States</option>
              <option value="Canada">Canada</option>
              <option value="UK">United Kingdom</option>
              <option value="Australia">Australia</option>
              <option value="India">India</option>
            </select>
          </div>
        </div>
        
        <div className="shipping-form-buttons">
          <button 
            className="back-btn"
            onClick={() => setCheckoutStep(1)}
          >
            Back to Cart
          </button>
          <button 
            className="continue-btn"
            onClick={proceedToPayment}
            disabled={!isAddressComplete()}
          >
            Continue to Payment
          </button>
        </div>
      </div>
    );
  };

  const renderPaymentForm = () => {
    return (
      <div className="payment-form">
        <h3>Payment Method</h3>
        
        <div className="payment-options">
          <div className="payment-option">
            <input
              type="radio"
              id="cash"
              name="paymentMethod"
              value="cash"
              checked={paymentMethod === 'cash'}
              onChange={handlePaymentMethodChange}
            />
            <label htmlFor="cash">Cash on Delivery</label>
          </div>
          
          <div className="payment-option">
            <input
              type="radio"
              id="credit"
              name="paymentMethod"
              value="credit"
              checked={paymentMethod === 'credit'}
              onChange={handlePaymentMethodChange}
            />
            <label htmlFor="credit">Credit Card</label>
          </div>
          
          <div className="payment-option">
            <input
              type="radio"
              id="paypal"
              name="paymentMethod"
              value="paypal"
              checked={paymentMethod === 'paypal'}
              onChange={handlePaymentMethodChange}
            />
            <label htmlFor="paypal">PayPal</label>
          </div>
        </div>
        
        {paymentMethod === 'credit' && (
          <div className="credit-card-form">
            <div className="form-group">
              <label htmlFor="cardNumber">Card Number</label>
              <input
                type="text"
                id="cardNumber"
                placeholder="1234 5678 9012 3456"
                maxLength="19"
              />
            </div>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="expDate">Expiration Date</label>
                <input
                  type="text"
                  id="expDate"
                  placeholder="MM/YY"
                  maxLength="5"
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="cvv">CVV</label>
                <input
                  type="text"
                  id="cvv"
                  placeholder="123"
                  maxLength="3"
                />
              </div>
            </div>
            
            <div className="form-group">
              <label htmlFor="cardName">Name on Card</label>
              <input
                type="text"
                id="cardName"
                placeholder="John Smith"
              />
            </div>
          </div>
        )}
        
        {error && <div className="error-message">{error}</div>}
        
        <div className="payment-form-buttons">
          <button 
            className="back-btn"
            onClick={() => setCheckoutStep(2)}
          >
            Back to Shipping
          </button>
          <button 
            className="place-order-btn"
            onClick={handleCheckout}
            disabled={loading}
          >
            {loading ? 'Processing...' : 'Place Order'}
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="shopping-cart">
      <div className="checkout-steps">
        <div className={`step ${checkoutStep >= 1 ? 'active' : ''}`}>
          <div className="step-number">1</div>
          <span>Shopping Cart</span>
        </div>
        <div className="step-connector"></div>
        <div className={`step ${checkoutStep >= 2 ? 'active' : ''}`}>
          <div className="step-number">2</div>
          <span>Shipping</span>
        </div>
        <div className="step-connector"></div>
        <div className={`step ${checkoutStep >= 3 ? 'active' : ''}`}>
          <div className="step-number">3</div>
          <span>Payment</span>
        </div>
      </div>
      
      <div className="shopping-cart-content">
        <div className="cart-main">
          {checkoutStep === 1 && renderCartItems()}
          {checkoutStep === 2 && renderShippingForm()}
          {checkoutStep === 3 && renderPaymentForm()}
        </div>
        
        {(checkoutStep === 1 || cartItems.length > 0) && (
          <div className="cart-sidebar">
            {renderCartSummary()}
          </div>
        )}
      </div>
    </div>
  );
};

export default ShoppingCart;
