import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import '../styles/ManageOrders.css';

const ManageOrders = () => {
  const { token } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [selectedOrder, setSelectedOrder] = useState(null);

  useEffect(() => {
    fetchOrders();
  }, [token]);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/orders/admin', {
        headers: {
          'x-auth-token': token
        }
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to fetch orders');
      }

      setOrders(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      const response = await fetch(`http://localhost:5000/api/orders/${orderId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': token
        },
        body: JSON.stringify({ status: newStatus })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to update order status');
      }

      // Update orders in state
      setOrders(orders.map(order => 
        order._id === orderId ? { ...order, status: newStatus } : order
      ));
      
      setSuccessMessage(`Order status updated to ${newStatus}`);
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccessMessage('');
      }, 3000);
    } catch (err) {
      setError(err.message);
    }
  };

  const viewOrderDetails = (order) => {
    setSelectedOrder(order);
  };

  const closeOrderDetails = () => {
    setSelectedOrder(null);
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return <div className="loading">Loading orders...</div>;
  }

  return (
    <div className="manage-orders-container">
      <h2>Manage Orders</h2>
      
      {error && <div className="error-message">{error}</div>}
      {successMessage && <div className="success-message">{successMessage}</div>}
      
      {selectedOrder ? (
        <div className="order-details">
          <div className="order-details-header">
            <h3>Order Details</h3>
            <button className="close-btn" onClick={closeOrderDetails}>×</button>
          </div>
          
          <div className="order-info">
            <p><strong>Order ID:</strong> {selectedOrder._id}</p>
            <p><strong>Date:</strong> {formatDate(selectedOrder.createdAt)}</p>
            <p><strong>Customer:</strong> {selectedOrder.user?.name || 'N/A'}</p>
            <p><strong>Email:</strong> {selectedOrder.user?.email || 'N/A'}</p>
            <p><strong>Status:</strong> <span className={`status-${selectedOrder.status}`}>{selectedOrder.status}</span></p>
            <p><strong>Total Amount:</strong> ${selectedOrder.totalAmount.toFixed(2)}</p>
          </div>

          <div className="shipping-address">
            <h4>Shipping Address</h4>
            <p>{selectedOrder.shippingAddress.address}</p>
            <p>{selectedOrder.shippingAddress.city}, {selectedOrder.shippingAddress.postalCode}</p>
            <p>{selectedOrder.shippingAddress.country}</p>
          </div>
          
          <h4>Products</h4>
          <table className="order-products-table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody>
              {selectedOrder.products.map((item, index) => (
                <tr key={index}>
                  <td>{item.name}</td>
                  <td>${item.price.toFixed(2)}</td>
                  <td>{item.quantity}</td>
                  <td>${(item.price * item.quantity).toFixed(2)}</td>
                </tr>
              ))}
            </tbody>
          </table>
          
          <div className="update-status">
            <h4>Update Order Status</h4>
            <div className="status-buttons">
              <button 
                className={`status-btn pending ${selectedOrder.status === 'pending' ? 'active' : ''}`}
                onClick={() => handleStatusChange(selectedOrder._id, 'pending')}
                disabled={selectedOrder.status === 'pending'}
              >
                Pending
              </button>
              <button 
                className={`status-btn processing ${selectedOrder.status === 'processing' ? 'active' : ''}`}
                onClick={() => handleStatusChange(selectedOrder._id, 'processing')}
                disabled={selectedOrder.status === 'processing'}
              >
                Processing
              </button>
              <button 
                className={`status-btn shipped ${selectedOrder.status === 'shipped' ? 'active' : ''}`}
                onClick={() => handleStatusChange(selectedOrder._id, 'shipped')}
                disabled={selectedOrder.status === 'shipped'}
              >
                Shipped
              </button>
              <button 
                className={`status-btn delivered ${selectedOrder.status === 'delivered' ? 'active' : ''}`}
                onClick={() => handleStatusChange(selectedOrder._id, 'delivered')}
                disabled={selectedOrder.status === 'delivered'}
              >
                Delivered
              </button>
              <button 
                className={`status-btn cancelled ${selectedOrder.status === 'cancelled' ? 'active' : ''}`}
                onClick={() => handleStatusChange(selectedOrder._id, 'cancelled')}
                disabled={selectedOrder.status === 'cancelled'}
              >
                Cancelled
              </button>
            </div>
          </div>
        </div>
      ) : (
        <div className="orders-table-container">
          <table className="orders-table">
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Customer</th>
                <th>Date</th>
                <th>Total</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.length === 0 ? (
                <tr>
                  <td colSpan="6" className="no-orders">No orders available</td>
                </tr>
              ) : (
                orders.map(order => (
                  <tr key={order._id}>
                    <td className="order-id">{order._id.substring(order._id.length - 8)}</td>
                    <td>{order.user?.name || 'N/A'}</td>
                    <td>{formatDate(order.createdAt)}</td>
                    <td className="order-total">${order.totalAmount.toFixed(2)}</td>
                    <td className="order-status">
                      <span className={`status-badge ${order.status}`}>
                        {order.status}
                      </span>
                    </td>
                    <td className="actions-cell">
                      <button 
                        className="view-btn"
                        onClick={() => viewOrderDetails(order)}
                      >
                        View Details
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ManageOrders;
