import React, { useContext, useState } from 'react';
import { CartContext } from '../context/CartContext';
import { FaShoppingCart, FaCheck } from 'react-icons/fa';
import '../styles/AddToCartButton.css';

const AddToCartButton = ({ product, quantity = 1, className = "", showQuantity = false }) => {
  const { addToCart } = useContext(CartContext);
  const [added, setAdded] = useState(false);
  const [itemQuantity, setItemQuantity] = useState(quantity);

  const handleAddToCart = () => {
    addToCart(product, itemQuantity);
    
    // Show success animation
    setAdded(true);
    setTimeout(() => setAdded(false), 1500);
  };

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (value > 0) {
      setItemQuantity(value);
    }
  };

  return (
    <div className={`add-to-cart-container ${className}`}>
      {showQuantity && (
        <div className="quantity-selector">
          <button
            className="quantity-btn" 
            onClick={() => itemQuantity > 1 && setItemQuantity(itemQuantity - 1)}
          >
            -
          </button>
          <input
            type="number"
            min="1"
            value={itemQuantity}
            onChange={handleQuantityChange}
            className="quantity-input"
          />
          <button 
            className="quantity-btn"
            onClick={() => setItemQuantity(itemQuantity + 1)}
          >
            +
          </button>
        </div>
      )}
      
      <button 
        className={`add-to-cart-btn ${added ? 'added' : ''}`}
        onClick={handleAddToCart}
        disabled={added}
      >
        {added ? (
          <>
            <FaCheck className="add-icon" /> Added!
          </>
        ) : (
          <>
            <FaShoppingCart className="add-icon" /> Add to Cart
          </>
        )}
      </button>
    </div>
  );
};

export default AddToCartButton;
