import React, { useState, useContext } from 'react';
import { Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import ProductList from '../components/ProductList';
import ShoppingCart from '../components/user/ShoppingCart';
import MyOrders from '../components/user/MyOrders';
import UserProfile from '../components/user/UserProfile';
import { CartProvider } from '../context/CartContext';
import '../styles/Dashboard.css';

// Icons
import { FaHome, FaShoppingCart, FaClipboardList, FaUser, FaBars, FaTimes, FaSignOutAlt } from 'react-icons/fa';

const UserDashboard = () => {
  const { currentUser, logout } = useContext(AuthContext);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const navigate = useNavigate();
  const location = useLocation();
  const currentDateTime = '2025-03-26 15:12:24';

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const isActive = (path) => {
    return location.pathname === path || location.pathname.startsWith(path + '/');
  };

  return (
    <CartProvider>
      <div className="user-dashboard">
        {/* Mobile Menu Toggle */}
        <button className="mobile-menu-toggle" onClick={toggleSidebar}>
          {sidebarOpen ? <FaTimes /> : <FaBars />}
        </button>

        {/* Sidebar */}
        <div className={`user-sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
          <div className="user-sidebar-header">
            <h2>Grocery Shop</h2>
          </div>

          <div className="user-sidebar-user">
            <div className="avatar-circle">
              <FaUser />
            </div>
            <div className="user-info">
              <h3>{currentUser?.name || 'Krizzna69'}</h3>
              <span>Customer</span>
            </div>
          </div>

          <nav className="user-sidebar-nav">
            <ul>
              <li className={isActive('/user') && !isActive('/user/cart') ? 'active' : ''}>
                <Link to="/user" onClick={() => isMobile && setSidebarOpen(false)}>
                  <FaHome /> <span>Products</span>
                </Link>
              </li>
              <li className={isActive('/user/cart') ? 'active' : ''}>
                <Link to="/user/cart" onClick={() => isMobile && setSidebarOpen(false)}>
                  <FaShoppingCart /> <span>Shopping Cart</span>
                </Link>
              </li>
              <li className={isActive('/user/orders') ? 'active' : ''}>
                <Link to="/user/orders" onClick={() => isMobile && setSidebarOpen(false)}>
                  <FaClipboardList /> <span>My Orders</span>
                </Link>
              </li>
              <li className={isActive('/user/profile') ? 'active' : ''}>
                <Link to="/user/profile" onClick={() => isMobile && setSidebarOpen(false)}>
                  <FaUser /> <span>My Profile</span>
                </Link>
              </li>
            </ul>
          </nav>
          
          <div className="user-sidebar-footer">
            <button onClick={handleLogout} className="logout-button">
              <FaSignOutAlt /> <span>Logout</span>
            </button>
          </div>
        </div>

        {/* Main Content */}
        <div className="user-content">
          {/* Top Bar */}
          <div className="user-topbar">
            <div className="user-breadcrumb">
              <h1>
                {isActive('/user') && !isActive('/user/cart') && 'Shop Products'}
                {isActive('/user/cart') && 'Shopping Cart'}
                {isActive('/user/orders') && 'My Orders'}
                {isActive('/user/profile') && 'My Profile'}
              </h1>
            </div>
            <div className="user-topbar-right">
              <div className="user-datetime">{currentDateTime}</div>
            </div>
          </div>

          {/* Dashboard Content */}
          <div className="user-main-content">
            <Routes>
              <Route path="/" element={<ProductList />} />
              <Route path="/cart" element={<ShoppingCart />} />
              <Route path="/orders" element={<MyOrders />} />
              <Route path="/profile" element={<UserProfile />} />
            </Routes>
          </div>
        </div>
      </div>
    </CartProvider>
  );
};

export default UserDashboard;
