import React, { useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FaHome, FaUsers, FaClipboardList, FaBoxOpen, FaSignOutAlt } from 'react-icons/fa';
import { AuthContext } from '../context/AuthContext';
import '../styles/Sidebar.css';

const AdminSidebar = () => {
  const { logout } = useContext(AuthContext);
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path || location.pathname.startsWith(`${path}/`);
  };

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h2>Admin Panel</h2>
      </div>

      <nav className="sidebar-nav">
        <ul>
          <li>
            <Link to="/admin" className={isActive('/admin') && !isActive('/admin/users') && !isActive('/admin/products') ? 'active' : ''}>
              <FaHome /> <span>Dashboard</span>
            </Link>
          </li>
          <li>
            <Link to="/admin/orders" className={isActive('/admin/orders') ? 'active' : ''}>
              <FaClipboardList /> <span>Orders</span>
            </Link>
          </li>
          <li>
            <Link to="/admin/users" className={isActive('/admin/users') ? 'active' : ''}>
              <FaUsers /> <span>Users</span>
            </Link>
          </li>
          <li>
            <Link to="/admin/products" className={isActive('/admin/products') ? 'active' : ''}>
              <FaBoxOpen /> <span>Products</span>
            </Link>
          </li>
        </ul>
      </nav>

      <div className="sidebar-footer">
        <button onClick={logout} className="logout-button">
          <FaSignOutAlt /> <span>Logout</span>
        </button>
      </div>
    </div>
  );
};

export default AdminSidebar;
