import React, { useState, useEffect, useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { FaBox, FaClock, FaInfoCircle, FaArrowLeft } from 'react-icons/fa';
import '../styles/Orders.css';

const Orders = () => {
  const { token } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentDateTime] = useState('2025-03-27 04:21:46');
  const [currentUser] = useState('Krizzna69');
  const location = useLocation();
  
  // Check if a new order was just placed
  const newOrderId = location.state?.newOrderId;
  
  useEffect(() => {
    fetchOrders();
  }, [token]);
  
  const fetchOrders = async () => {
    setLoading(true);
    
    try {
      // Try to fetch real orders from API (this may fail if backend isn't ready)
      try {
        const response = await fetch('http://localhost:5000/api/orders', {
          headers: {
            'x-auth-token': token
          }
        });
        
        if (response.ok) {
          const data = await response.json();
          setOrders(data);
          setLoading(false);
          return;
        }
      } catch (err) {
        console.log("API not available, using mock data");
      }
      
      // Use mock data as fallback
      // Check if there's a new order from localStorage
      const savedNewOrder = localStorage.getItem('lastOrder');
      let mockOrders = [
        {
          _id: 'ORD12345678',
          items: [
            {
              product: {
                _id: '1',
                name: 'Fresh Organic Apples',
                price: 3.99,
                imageUrl: 'https://images.unsplash.com/photo-1567306226416-28f0efdc88ce?auto=format&fit=crop&w=300'
              },
              quantity: 2,
              price: 3.99
            },
            {
              product: {
                _id: '2',
                name: 'Whole Grain Bread',
                price: 4.50,
                imageUrl: 'https://images.unsplash.com/photo-1598373182133-52452f7691ef?auto=format&fit=crop&w=300'
              },
              quantity: 1,
              price: 4.50
            }
          ],
          shippingAddress: {
            fullName: 'John Doe',
            phoneNumber: '555-123-4567',
            address: '123 Main St',
            city: 'Example City',
            state: 'Example State',
            zipCode: '12345'
          },
          paymentMethod: 'COD',
          totalAmount: 12.48,
          status: 'Processing',
          createdAt: '2025-03-26T14:30:00.000Z'
        }
      ];
      
      // Add the new order from checkout if it exists
      if (savedNewOrder) {
        try {
          const newOrder = JSON.parse(savedNewOrder);
          mockOrders = [newOrder, ...mockOrders];
        } catch (error) {
          console.error("Error parsing saved order", error);
        }
      }
      
      
    } catch (err) {
      console.error("Error fetching orders:", err);
      setError('Failed to load orders. Please try again later.');
    } finally {
      setLoading(false);
    }
  };
  
  const formatDate = (dateString) => {
    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };
  
  const formatPrice = (price) => {
    return `$${price.toFixed(2)}`;
  };
  
  if (loading) {
    return <div className="loading">Loading your orders...</div>;
  }
  
  if (error) {
    return <div className="error-message">{error}</div>;
  }
  
  if (orders.length === 0) {
    return (
      <div className="no-orders">
        <FaBox className="no-orders-icon" />
        <h2>No Orders Found</h2>
        <p>You haven't placed any orders yet.</p>
        <Link to="/user" className="shop-now-btn">
          <FaArrowLeft /> Shop Now
        </Link>
      </div>
    );
  }
  
  return (
    <div className="orders-page">
      <div className="orders-header">
        <h1>My Orders</h1>
        <div className="orders-user-info">
          <span>{currentDateTime}</span>
          <span>User: {currentUser}</span>
        </div>
      </div>
      
      <div className="orders-list">
        {orders.map(order => (
          <div 
            key={order._id} 
            className={`order-card ${newOrderId === order._id ? 'new-order' : ''}`}
            id={newOrderId === order._id ? 'new-order' : undefined}
          >
            {newOrderId === order._id && (
              <div className="new-order-badge">New Order</div>
            )}
            
            <div className="order-header">
              <div className="order-info">
                <h3>Order #{order._id}</h3>
                <p className="order-date">Placed on {formatDate(order.createdAt)}</p>
              </div>
              <div className="order-status">
                <span className={`status-badge ${order.status.toLowerCase()}`}>
                  {order.status}
                </span>
                <span className="order-total">{formatPrice(order.totalAmount)}</span>
              </div>
            </div>
            
            <div className="order-items">
              {order.items.map((item, index) => (
                <div key={index} className="order-item">
                  <div className="item-image">
                    <img 
                      src={item.product.imageUrl || 'https://via.placeholder.com/60'} 
                      alt={item.product.name} 
                    />
                  </div>
                  <div className="item-details">
                    <h4>{item.product.name}</h4>
                    <p className="item-price">
                      {formatPrice(item.price)} x {item.quantity}
                    </p>
                  </div>
                  <div className="item-total">
                    {formatPrice(item.price * item.quantity)}
                  </div>
                </div>
              ))}
            </div>
            
            <div className="order-footer">
              <div className="shipping-info">
                <h4>Shipping Address</h4>
                <p>{order.shippingAddress.fullName}</p>
                <p>{order.shippingAddress.address}</p>
                <p>{order.shippingAddress.city}, {order.shippingAddress.state} {order.shippingAddress.zipCode}</p>
                {order.shippingAddress.phoneNumber && (
                  <p>Phone: {order.shippingAddress.phoneNumber}</p>
                )}
              </div>
              
              <div className="payment-info">
                <h4>Payment Method</h4>
                <p>{order.paymentMethod === 'COD' ? 'Cash on Delivery' : order.paymentMethod}</p>
                
                <h4 className="order-summary-title">Order Summary</h4>
                <div className="order-summary">
                  <div className="summary-row">
                    <span>Items Total:</span>
                    <span>{formatPrice(order.items.reduce((sum, item) => sum + (item.price * item.quantity), 0))}</span>
                  </div>
                  <div className="summary-row">
                    <span>Shipping:</span>
                    <span>{order.totalAmount > order.items.reduce((sum, item) => sum + (item.price * item.quantity), 0) ? 
                      formatPrice(order.totalAmount - order.items.reduce((sum, item) => sum + (item.price * item.quantity), 0)) : 
                      'Free'}</span>
                  </div>
                  <div className="summary-divider"></div>
                  <div className="summary-row total">
                    <span>Total:</span>
                    <span>{formatPrice(order.totalAmount)}</span>
                  </div>
                </div>
              </div>
            </div>
            
            <div className="order-actions">
              <button className="track-order-btn">
                Track Order
              </button>
              <button className="order-help-btn">
                <FaInfoCircle /> Need Help?
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Orders;
