import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import AddToCartButton from './AddToCartButton';
import { FaShoppingCart, FaUser, FaClock } from 'react-icons/fa';
import { syncCartWithBackend } from '../utils/cartUtils';
import '../styles/ProductList.css';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { token, user } = useContext(AuthContext);
  const [cart, setCart] = useState([]);
  const [currentDateTime] = useState('2025-03-27 03:19:36');
  const [currentUser] = useState('Krizzna69');

  // Fetch products and load cart
  useEffect(() => {
    fetchProducts();
    loadUserCart();
  }, [user?.id]);
  
  // Load the user's cart (from backend if logged in, otherwise localStorage)
  const loadUserCart = async () => {
    if (user?.id && token) {
      try {
        // Try to get cart from backend
        const response = await fetch('http://localhost:5000/api/cart', {
          headers: {
            'x-auth-token': token
          }
        });
        
        if (response.ok) {
          const data = await response.json();
          // Convert backend cart format to frontend format
          const cartItems = data.items.map(item => ({
            _id: item.product._id,
            name: item.product.name,
            price: item.product.price,
            category: item.product.category,
            imageUrl: item.product.imageUrl,
            quantity: item.quantity
          }));
          setCart(cartItems);
          return;
        }
      } catch (error) {
        console.error("Error loading cart from backend:", error);
      }
    }
    
    // Fallback to localStorage if no user or backend request failed
    const savedCart = localStorage.getItem(`cart_${user?.id || 'guest'}`);
    if (savedCart) {
      try {
        setCart(JSON.parse(savedCart));
      } catch (error) {
        console.error("Error parsing cart data:", error);
        setCart([]);
      }
    }
  };

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem(`cart_${user?.id || 'guest'}`, JSON.stringify(cart));
  }, [cart, user?.id]);

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/products');
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to fetch products');
      }

      setProducts(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const addToCart = async (product) => {
    // First update local state for immediate UI feedback
    setCart(prevCart => {
      // Check if product is already in cart
      const existingProduct = prevCart.find(item => item._id === product._id);
      
      if (existingProduct) {
        // Increase quantity if already in cart
        return prevCart.map(item => 
          item._id === product._id 
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        // Add product with quantity 1
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });

    // If user is logged in, sync with backend
    if (user?.id && token) {
      try {
        // Add the new item to backend cart
        await fetch('http://localhost:5000/api/cart', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'x-auth-token': token
          },
          body: JSON.stringify({
            productId: product._id,
            quantity: 1
          })
        });
      } catch (error) {
        console.error("Failed to sync cart with backend:", error);
      }
    }
  };

  const cartItemCount = cart.reduce((total, item) => total + item.quantity, 0);

  if (loading) {
    return <div className="loading">Loading products...</div>;
  }

  if (error) {
    return <div className="error-message">Error: {error}</div>;
  }

  if (products.length === 0) {
    return <div className="no-products">No products available.</div>;
  }

  return (
    <div className="product-list-container">
      <div className="product-list-header">
        <h2>Available Products</h2>
        <div className="user-info">
          <div className="datetime-info">
            <FaClock className="info-icon" />
            <span>{currentDateTime}</span>
          </div>
          <div className="username-info">
            <FaUser className="info-icon" />
            <span>{currentUser}</span>
          </div>
          <div className="cart-info">
            <FaShoppingCart className="info-icon" />
            <span className="cart-count">{cartItemCount}</span>
          </div>
        </div>
      </div>
      
      <div className="product-grid">
        {products.map(product => (
          <div key={product._id} className="product-card">
            <div className="product-image">
              {product.imageUrl ? (
                <img src={product.imageUrl} alt={product.name} />
              ) : (
                <div className="no-image">No Image</div>
              )}
            </div>
            <div className="product-details">
              <h3 className="product-name">{product.name}</h3>
              <p className="product-category">{product.category}</p>
              <p className="product-price">${product.price.toFixed(2)}</p>
              <p className="product-stock">
                {product.stockQuantity > 0 
                  ? `In Stock: ${product.stockQuantity}` 
                  : 'Out of Stock'}
              </p>
              <AddToCartButton 
                product={product}
                onAddToCart={addToCart}
                disabled={product.stockQuantity <= 0}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
