import React, { useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FaHome, FaShoppingCart, FaClipboardList, FaUser, FaSignOutAlt } from 'react-icons/fa';
import { AuthContext } from '../context/AuthContext';
import { CartContext } from '../context/CartContext';
import '../styles/Sidebar.css';

const UserSidebar = () => {
  const { logout } = useContext(AuthContext);
  const { cartCount } = useContext(CartContext);
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path || location.pathname.startsWith(`${path}/`);
  };

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h2>Grocery Shop</h2>
      </div>

      <nav className="sidebar-nav">
        <ul>
          <li>
            <Link to="/user" className={isActive('/user') && !isActive('/user/cart') ? 'active' : ''}>
              <FaHome /> <span>Shop</span>
            </Link>
          </li>
          <li>
            <Link to="/user/cart" className={isActive('/user/cart') ? 'active' : ''}>
              <FaShoppingCart /> <span>Cart</span>
              {cartCount > 0 && <span className="badge">{cartCount}</span>}
            </Link>
          </li>
          <li>
            <Link to="/user/orders" className={isActive('/user/orders') ? 'active' : ''}>
              <FaClipboardList /> <span>Orders</span>
            </Link>
          </li>
          <li>
            <Link to="/user/profile" className={isActive('/user/profile') ? 'active' : ''}>
              <FaUser /> <span>Profile</span>
            </Link>
          </li>
        </ul>
      </nav>

      <div className="sidebar-footer">
        <button onClick={logout} className="logout-button">
          <FaSignOutAlt /> <span>Logout</span>
        </button>
      </div>
    </div>
  );
};

export default UserSidebar;
