import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { FaSearch, FaUserEdit, FaUserLock, FaUserCheck } from 'react-icons/fa';
import '../styles/ManageUsers.css';

const UserManagement = () => {
  const { token } = useContext(AuthContext);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('');
  const [selectedUser, setSelectedUser] = useState(null);
  const [editMode, setEditMode] = useState(false);

  // Mock data to use while backend is being developed
  const mockUsers = [
    {
      _id: '1',
      name: 'John Smith',
      email: 'john.smith@example.com',
      role: 'user',
      createdAt: '2025-02-15T10:30:00Z'
    },
    {
      _id: '2',
      name: 'Sarah Johnson',
      email: 'sarah.johnson@example.com',
      role: 'user',
      createdAt: '2025-02-20T14:45:00Z'
    },
    {
      _id: '3',
      name: 'Michael Brown',
      email: 'michael.brown@example.com',
      role: 'admin',
      createdAt: '2025-01-10T09:15:00Z'
    },
    {
      _id: '4',
      name: 'Emily Davis',
      email: 'emily.davis@example.com',
      role: 'user',
      createdAt: '2025-03-05T16:20:00Z'
    },
    {
      _id: '5',
      name: 'David Wilson',
      email: 'david.wilson@example.com',
      role: 'user',
      createdAt: '2025-03-12T11:10:00Z'
    }
  ];

  useEffect(() => {
    fetchUsers();
  }, [token]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      // Comment out the actual API call for now and use mock data
      // In a real application, uncomment this code and remove the mock data
      /*
      const response = await fetch('http://localhost:5000/api/users', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'x-auth-token': token
        }
      });

      if (!response.ok) {
        throw new Error(`Server responded with status: ${response.status}`);
      }

      const data = await response.json();
      setUsers(data);
      */
      
      // Using mock data instead
      setTimeout(() => {
        setUsers(mockUsers);
        setLoading(false);
      }, 1000);
      
    } catch (err) {
      console.error("Error fetching users:", err);
      setError('Failed to fetch users. Please try again later.');
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'short', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const handleRoleChange = async (userId, newRole) => {
    try {
      // In a real app, you would make an API call to update the user role
      // For this example, we'll just update the local state
      setUsers(users.map(user => 
        user._id === userId ? { ...user, role: newRole } : user
      ));
    } catch (err) {
      setError('Failed to update user role');
    }
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleRoleFilter = (e) => {
    setRoleFilter(e.target.value);
  };

  // Filter users based on search term and role
  const filteredUsers = users.filter(user => {
    const matchesSearch = searchTerm === '' || 
      user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.email.toLowerCase().includes(searchTerm.toLowerCase());
    
    const matchesRole = roleFilter === '' || user.role === roleFilter;
    
    return matchesSearch && matchesRole;
  });

  if (loading) {
    return <div className="loading-message">Loading users...</div>;
  }

  if (error) {
    return <div className="error-message">Error: {error}</div>;
  }

  return (
    <div className="user-management">
      <div className="user-management-header">
        <div className="search-and-filter">
          <div className="search-container">
            <FaSearch className="search-icon" />
            <input
              type="text"
              placeholder="Search users by name or email..."
              value={searchTerm}
              onChange={handleSearch}
              className="search-input"
            />
          </div>
          
          <div className="filter-container">
            <select 
              value={roleFilter} 
              onChange={handleRoleFilter}
              className="role-filter"
            >
              <option value="">All Roles</option>
              <option value="admin">Admin</option>
              <option value="user">User</option>
            </select>
          </div>
        </div>
      </div>

      {filteredUsers.length === 0 ? (
        <div className="no-users-message">
          No users found. {searchTerm || roleFilter ? 'Try adjusting your filters.' : ''}
        </div>
      ) : (
        <div className="users-table-container">
          <table className="users-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Joined Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredUsers.map(user => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>
                    <span className={`role-badge ${user.role}`}>
                      {user.role === 'admin' ? 'Admin' : 'User'}
                    </span>
                  </td>
                  <td>{formatDate(user.createdAt)}</td>
                  <td className="actions-cell">
                    <button 
                      className="edit-role-btn"
                      onClick={() => handleRoleChange(
                        user._id, 
                        user.role === 'admin' ? 'user' : 'admin'
                      )}
                    >
                      {user.role === 'admin' ? (
                        <>
                          <FaUserLock /> Make User
                        </>
                      ) : (
                        <>
                          <FaUserCheck /> Make Admin
                        </>
                      )}
                    </button>
                    <button
                      className="edit-user-btn"
                      onClick={() => {
                        setSelectedUser(user);
                        setEditMode(true);
                      }}
                    >
                      <FaUserEdit /> Edit
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* User Edit Modal - Simplified version */}
      {selectedUser && editMode && (
        <div className="user-edit-modal">
          <div className="user-edit-content">
            <div className="user-edit-header">
              <h3>Edit User</h3>
              <button className="close-modal" onClick={() => setEditMode(false)}>×</button>
            </div>
            <div className="user-edit-body">
              <p>Editing user: {selectedUser.name}</p>
              <p>This feature is under development.</p>
            </div>
            <div className="user-edit-footer">
              <button className="close-btn" onClick={() => setEditMode(false)}>Close</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserManagement;
