import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import { FaSearch, FaFilter, FaStar } from 'react-icons/fa';
import AddToCartButton from './AddToCartButton';
import '../styles/ProductList.css';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const [priceRange, setPriceRange] = useState({ min: 0, max: 100 });
  const [sortBy, setSortBy] = useState('name');
  const [currentDateTime] = useState('2025-03-26 15:58:25');
  const [currentUser] = useState('Krizzna69');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        
        // Attempt to fetch from backend API first
        try {
          const response = await fetch('http://localhost:5000/api/products');
          if (response.ok) {
            const data = await response.json();
            setProducts(data);
            setLoading(false);
            return;
          }
        } catch (error) {
          console.log('API not available, using mock data');
        }
        
        // Mock data as fallback
        const mockProducts = [
          {
            _id: '1',
            name: 'Fresh Organic Apples',
            description: 'Sweet and juicy organic apples. Perfect for snacking or baking.',
            price: 3.99,
            category: 'Fruits',
            image: 'https://images.unsplash.com/photo-1567306226416-28f0efdc88ce',
            rating: 4.5,
            inStock: true
          },
          {
            _id: '2',
            name: 'Whole Grain Bread',
            description: 'Nutritious whole grain bread made with organic flour.',
            price: 4.50,
            category: 'Bakery',
            image: 'https://images.unsplash.com/photo-1598373182133-52452f7691ef',
            rating: 4.2,
            inStock: true
          },
          {
            _id: '3',
            name: 'Organic Milk',
            description: 'Fresh organic milk from grass-fed cows. No hormones or antibiotics.',
            price: 3.75,
            category: 'Dairy',
            image: 'https://images.unsplash.com/photo-1550583724-b2692b85b150',
            rating: 4.8,
            inStock: true
          },
          {
            _id: '4',
            name: 'Free-Range Eggs',
            description: 'Farm fresh free-range eggs from humanely raised chickens.',
            price: 5.25,
            category: 'Dairy',
            image: 'https://images.unsplash.com/photo-1516448620398-c5f44bf9f441',
            rating: 4.6,
            inStock: true
          },
          {
            _id: '5',
            name: 'Organic Spinach',
            description: 'Fresh organic spinach, washed and ready to eat.',
            price: 2.99,
            category: 'Vegetables',
            image: 'https://images.unsplash.com/photo-1576045057995-568f588f82fb',
            rating: 4.3,
            inStock: true
          },
          {
            _id: '6',
            name: 'Fresh Atlantic Salmon',
            description: 'Wild-caught Atlantic salmon. Rich in omega-3 fatty acids.',
            price: 12.99,
            category: 'Seafood',
            image: 'https://images.unsplash.com/photo-1599084993091-1cb5c0721cc6',
            rating: 4.7,
            inStock: true
          }
        ];
        
        setTimeout(() => {
          setProducts(mockProducts);
          setLoading(false);
        }, 500);
      } catch (err) {
        setError('Failed to load products. Please try again later.');
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleCategoryFilter = (e) => {
    setCategoryFilter(e.target.value);
  };

  const handlePriceRangeChange = (e) => {
    const { name, value } = e.target;
    setPriceRange({ ...priceRange, [name]: parseFloat(value) });
  };

  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  // Get unique categories
  const categories = [...new Set(products.map(product => product.category))];

  // Filter and sort products
  const filteredProducts = products
    .filter(product => 
      product.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (categoryFilter === '' || product.category === categoryFilter) &&
      product.price >= priceRange.min &&
      product.price <= priceRange.max
    )
    .sort((a, b) => {
      if (sortBy === 'name') {
        return a.name.localeCompare(b.name);
      } else if (sortBy === 'price-low') {
        return a.price - b.price;
      } else if (sortBy === 'price-high') {
        return b.price - a.price;
      } else if (sortBy === 'rating') {
        return b.rating - a.rating;
      }
      return 0;
    });

  if (loading) {
    return <div className="loading">Loading products...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="product-list-container">
      <div className="product-list-header">
        <div className="shop-info">
          <span className="current-time">{currentDateTime}</span>
          <span className="shop-welcome">Welcome to our Grocery Shop</span>
        </div>
        <div className="product-search">
          <div className="search-input-container">
            <FaSearch className="search-icon" />
            <input
              type="text"
              placeholder="Search products..."
              value={searchTerm}
              onChange={handleSearch}
              className="search-input"
            />
          </div>
        </div>
      </div>

      <div className="product-list-content">
        <div className="filter-section">
          <div className="filter-header">
            <FaFilter />
            <h3>Filters</h3>
          </div>

          <div className="filter-group">
            <label>Category</label>
            <select value={categoryFilter} onChange={handleCategoryFilter}>
              <option value="">All Categories</option>
              {categories.map(category => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>Price Range</label>
            <div className="price-inputs">
              <div>
                <span>Min: ${priceRange.min}</span>
                <input
                  type="range"
                  name="min"
                  min="0"
                  max="50"
                  value={priceRange.min}
                  onChange={handlePriceRangeChange}
                />
              </div>
              <div>
                <span>Max: ${priceRange.max}</span>
                <input
                  type="range"
                  name="max"
                  min="0"
                  max="100"
                  value={priceRange.max}
                  onChange={handlePriceRangeChange}
                />
              </div>
            </div>
          </div>

          <div className="filter-group">
            <label>Sort By</label>
            <select value={sortBy} onChange={handleSortChange}>
              <option value="name">Name</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="rating">Rating</option>
            </select>
          </div>
        </div>

        <div className="product-grid">
          {filteredProducts.length === 0 ? (
            <div className="no-products">No products found matching your criteria.</div>
          ) : (
            filteredProducts.map(product => (
              <div key={product._id} className="product-card">
                <Link to={`/products/${product._id}`} className="product-image-container">
                  <img src={product.image} alt={product.name} className="product-image" />
                </Link>
                <div className="product-info">
                  <Link to={`/products/${product._id}`} className="product-name">{product.name}</Link>
                  <div className="product-category">{product.category}</div>
                  <div className="product-rating">
                    <FaStar className="star-icon" /> {product.rating}
                  </div>
                  <div className="product-price">${product.price.toFixed(2)}</div>
                  <AddToCartButton product={product} />
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductList;
