import React, { useState, useEffect, useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { FaBox, FaArrowLeft } from 'react-icons/fa';
import '../styles/Orders.css';

const Orders = () => {
  const { token } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentDateTime] = useState('2025-03-27 05:23:01');
  const [currentUser] = useState('Krizzna69');
  const location = useLocation();
  
  // Check if a new order was just placed
  const newOrderId = location.state?.newOrderId;
  
  useEffect(() => {
    fetchOrders();
  }, [token]);
  
  const fetchOrders = async () => {
    setLoading(true);
    
    try {
      // Try to fetch real orders from API
      try {
        const response = await fetch('http://localhost:5000/api/orders', {
          headers: {
            'x-auth-token': token
          }
        });
        
        if (response.ok) {
          const data = await response.json();
          setOrders(data);
          return;
        }
      } catch (err) {
        console.log("API not available, checking localStorage");
      }
      
      // Check for orders in localStorage
      let savedOrders = [];
      
      // First try to get multiple orders from allOrders
      const allOrdersStr = localStorage.getItem('allOrders');
      if (allOrdersStr) {
        try {
          const allOrders = JSON.parse(allOrdersStr);
          if (Array.isArray(allOrders)) {
            savedOrders = allOrders;
          }
        } catch (error) {
          console.error("Error parsing all orders", error);
        }
      }
      
      // If no orders found in allOrders, try lastOrder
      if (savedOrders.length === 0) {
        const singleOrderStr = localStorage.getItem('lastOrder');
        if (singleOrderStr) {
          try {
            const singleOrder = JSON.parse(singleOrderStr);
            savedOrders = [singleOrder];
          } catch (error) {
            console.error("Error parsing last order", error);
          }
        }
      }
      
      setOrders(savedOrders);
    } catch (err) {
      console.error("Error fetching orders:", err);
      setError('Failed to load orders. Please try again later.');
    } finally {
      setLoading(false);
    }
  };
  
  const formatDate = (dateString) => {
    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };
  
  const formatPrice = (price) => {
    // Add null check before calling toFixed
    return typeof price === 'number' ? `$${price.toFixed(2)}` : '$0.00';
  };
  
  if (loading) {
    return <div className="loading">Loading your orders...</div>;
  }
  
  if (error) {
    return <div className="error-message">{error}</div>;
  }
  
  if (!orders || orders.length === 0) {
    return (
      <div className="no-orders">
        <FaBox className="no-orders-icon" />
        <h2>No Orders Found</h2>
        <p>You haven't placed any orders yet.</p>
        <Link to="/user" className="shop-now-btn">
          <FaArrowLeft /> Shop Now
        </Link>
      </div>
    );
  }
  
  return (
    <div className="orders-page">
      <div className="orders-header">
        <h1>My Orders</h1>
        <div className="orders-user-info">
          <span>{currentDateTime}</span>
          <span>User: {currentUser}</span>
        </div>
      </div>
      
      <div className="orders-list">
        {orders.map(order => {
          // Check if order has required properties before rendering
          if (!order || !order._id || !order.items || !Array.isArray(order.items)) {
            return null;
          }
          
          return (
            <div 
              key={order._id} 
              className={`order-card ${newOrderId === order._id ? 'new-order' : ''}`}
              id={newOrderId === order._id ? 'new-order' : undefined}
            >
              {newOrderId === order._id && (
                <div className="new-order-badge">New Order</div>
              )}
              
              <div className="order-header">
                <div className="order-info">
                  <h3>Order #{order._id}</h3>
                  <p className="order-date">Placed on {order.createdAt ? formatDate(order.createdAt) : 'Unknown date'}</p>
                </div>
                <div className="order-status">
                  <span className={`status-badge ${(order.status || '').toLowerCase()}`}>
                    {order.status || 'Unknown'}
                  </span>
                  <span className="order-total">{formatPrice(order.totalAmount)}</span>
                </div>
              </div>
              
              <div className="order-items">
                {order.items.map((item, index) => {
                  // Check if item has required properties
                  if (!item || !item.product) {
                    return null;
                  }
                  
                  const itemPrice = typeof item.price === 'number' ? item.price : 0;
                  const itemQuantity = typeof item.quantity === 'number' ? item.quantity : 0;
                  
                  return (
                    <div key={index} className="order-item">
                      <div className="item-image">
                        <img 
                          src={item.product.imageUrl || 'https://via.placeholder.com/60'} 
                          alt={item.product.name || 'Product'} 
                        />
                      </div>
                      <div className="item-details">
                        <h4>{item.product.name || 'Unknown Product'}</h4>
                        <p className="item-price">
                          {formatPrice(itemPrice)} x {itemQuantity}
                        </p>
                      </div>
                      <div className="item-total">
                        {formatPrice(itemPrice * itemQuantity)}
                      </div>
                    </div>
                  );
                })}
              </div>
              
              <div className="order-footer">
                {order.shippingAddress && (
                  <div className="shipping-info">
                    <h4>Shipping Address</h4>
                    <p>{order.shippingAddress.fullName || 'N/A'}</p>
                    <p>{order.shippingAddress.address || 'N/A'}</p>
                    <p>
                      {order.shippingAddress.city || 'N/A'}, 
                      {order.shippingAddress.state || 'N/A'} 
                      {order.shippingAddress.zipCode || 'N/A'}
                    </p>
                    {order.shippingAddress.phoneNumber && (
                      <p>Phone: {order.shippingAddress.phoneNumber}</p>
                    )}
                  </div>
                )}
                
                <div className="payment-info">
                  <h4>Payment Method</h4>
                  <p>{order.paymentMethod === 'COD' ? 'Cash on Delivery' : (order.paymentMethod || 'N/A')}</p>
                  
                  <h4 className="order-summary-title">Order Summary</h4>
                  <div className="order-summary">
                    <div className="summary-row">
                      <span>Items Total:</span>
                      <span>{formatPrice(
                        Array.isArray(order.items) 
                          ? order.items.reduce((sum, item) => {
                              const itemPrice = typeof item.price === 'number' ? item.price : 0;
                              const itemQuantity = typeof item.quantity === 'number' ? item.quantity : 0;
                              return sum + (itemPrice * itemQuantity);
                            }, 0)
                          : 0
                      )}</span>
                    </div>
                    <div className="summary-row">
                      <span>Shipping:</span>
                      <span>{
                        typeof order.totalAmount === 'number' && Array.isArray(order.items) 
                          ? (
                              order.totalAmount > order.items.reduce((sum, item) => {
                                const itemPrice = typeof item.price === 'number' ? item.price : 0;
                                const itemQuantity = typeof item.quantity === 'number' ? item.quantity : 0;
                                return sum + (itemPrice * itemQuantity);
                              }, 0)
                                ? formatPrice(
                                    order.totalAmount - order.items.reduce((sum, item) => {
                                      const itemPrice = typeof item.price === 'number' ? item.price : 0;
                                      const itemQuantity = typeof item.quantity === 'number' ? item.quantity : 0;
                                      return sum + (itemPrice * itemQuantity);
                                    }, 0)
                                  )
                                : 'Free'
                            )
                          : 'Free'
                      }</span>
                    </div>
                    <div className="summary-divider"></div>
                    <div className="summary-row total">
                      <span>Total:</span>
                      <span>{formatPrice(order.totalAmount)}</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Orders;
