import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import '../styles/ManageOrders.css';

const ManageOrders = () => {
  const { token } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [currentDateTime] = useState('2025-03-27 05:23:01');
  const [currentUser] = useState('Krizzna69');

  useEffect(() => {
    fetchOrders();
  }, [token]);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      
      // Try to fetch from API first (if implemented)
      try {
        const response = await fetch('http://localhost:5000/api/orders/admin', {
          headers: {
            'x-auth-token': token
          }
        });

        if (response.ok) {
          const data = await response.json();
          setOrders(data);
          setLoading(false);
          return;
        }
      } catch (err) {
        console.log("API not available, using localStorage");
      }
      
      // Fallback to localStorage if API fails - get ALL orders
      const allOrders = localStorage.getItem('allOrders');
      if (allOrders) {
        try {
          const parsedOrders = JSON.parse(allOrders);
          if (Array.isArray(parsedOrders)) {
            setOrders(parsedOrders);
          } else {
            // If it's not an array, check if there's a single order
            const singleOrder = localStorage.getItem('lastOrder');
            if (singleOrder) {
              try {
                const parsedOrder = JSON.parse(singleOrder);
                setOrders([parsedOrder]);
              } catch (err) {
                console.error("Error parsing single order", err);
                setOrders([]);
              }
            } else {
              setOrders([]);
            }
          }
        } catch (err) {
          console.error("Error parsing all orders", err);
          setOrders([]);
        }
      } else {
        // If there's no allOrders array, try to get the single last order
        const singleOrder = localStorage.getItem('lastOrder');
        if (singleOrder) {
          try {
            const parsedOrder = JSON.parse(singleOrder);
            setOrders([parsedOrder]);
          } catch (err) {
            console.error("Error parsing single order", err);
            setOrders([]);
          }
        } else {
          setOrders([]);
        }
      }
    } catch (err) {
      setError(err.message || 'Failed to fetch orders');
    } finally {
      setLoading(false);
    }
  };

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      // Try API first (if implemented)
      let apiSuccess = false;
      
      try {
        const response = await fetch(`http://localhost:5000/api/orders/${orderId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'x-auth-token': token
          },
          body: JSON.stringify({ status: newStatus })
        });

        if (response.ok) {
          apiSuccess = true;
        }
      } catch (err) {
        console.log("API not available, updating in localStorage");
      }
      
      // Update orders in state
      setOrders(orders.map(order => 
        order._id === orderId ? { ...order, status: newStatus } : order
      ));
      
      // If using localStorage, update both in lastOrder and allOrders
      if (!apiSuccess) {
        // Update single order if it matches
        const singleOrder = localStorage.getItem('lastOrder');
        if (singleOrder) {
          try {
            const parsedOrder = JSON.parse(singleOrder);
            if (parsedOrder._id === orderId) {
              parsedOrder.status = newStatus;
              localStorage.setItem('lastOrder', JSON.stringify(parsedOrder));
            }
          } catch (err) {
            console.error("Error updating single order in localStorage", err);
          }
        }
        
        // Update in allOrders array
        const allOrders = localStorage.getItem('allOrders');
        if (allOrders) {
          try {
            const parsedOrders = JSON.parse(allOrders);
            if (Array.isArray(parsedOrders)) {
              const updatedOrders = parsedOrders.map(order => 
                order._id === orderId ? { ...order, status: newStatus } : order
              );
              localStorage.setItem('allOrders', JSON.stringify(updatedOrders));
            }
          } catch (err) {
            console.error("Error updating order in allOrders", err);
          }
        }
      }
      
      // If selected order is open, update it too
      if (selectedOrder && selectedOrder._id === orderId) {
        setSelectedOrder({ ...selectedOrder, status: newStatus });
      }
      
      setSuccessMessage(`Order status updated to ${newStatus}`);
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccessMessage('');
      }, 3000);
    } catch (err) {
      setError(err.message || 'Failed to update order status');
    }
  };

  const viewOrderDetails = (order) => {
    setSelectedOrder(order);
  };

  const closeOrderDetails = () => {
    setSelectedOrder(null);
  };

  const formatDate = (dateString) => {
    try {
      const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
      return new Date(dateString).toLocaleDateString(undefined, options);
    } catch (err) {
      return 'Invalid date';
    }
  };
  
  const formatPrice = (price) => {
    // Add null check before calling toFixed
    return typeof price === 'number' ? `$${price.toFixed(2)}` : '$0.00';
  };

  if (loading) {
    return <div className="loading">Loading orders...</div>;
  }

  return (
    <div className="manage-orders-container">
      <div className="orders-header">
        <h2>Manage Orders</h2>
        <div className="admin-info">
          <span>{currentDateTime}</span>
          <span>Administrator: {currentUser}</span>
        </div>
      </div>
      
      {error && <div className="error-message">{error}</div>}
      {successMessage && <div className="success-message">{successMessage}</div>}
      
      {selectedOrder ? (
        <div className="order-details">
          <div className="order-details-header">
            <h3>Order Details</h3>
            <button className="close-btn" onClick={closeOrderDetails}>Ã—</button>
          </div>
          
          <div className="order-info">
            <p><strong>Order ID:</strong> {selectedOrder._id}</p>
            <p><strong>Date:</strong> {selectedOrder.createdAt ? formatDate(selectedOrder.createdAt) : 'Unknown date'}</p>
            <p><strong>Customer:</strong> {selectedOrder.shippingAddress?.fullName || 'N/A'}</p>
            <p><strong>Status:</strong> <span className={`status-${(selectedOrder.status || '').toLowerCase()}`}>{selectedOrder.status || 'Unknown'}</span></p>
            <p><strong>Total Amount:</strong> {formatPrice(selectedOrder.totalAmount)}</p>
          </div>

          {selectedOrder.shippingAddress && (
            <div className="shipping-address">
              <h4>Shipping Address</h4>
              <p>{selectedOrder.shippingAddress.address || 'N/A'}</p>
              <p>
                {selectedOrder.shippingAddress.city || 'N/A'}, 
                {selectedOrder.shippingAddress.state || 'N/A'} 
                {selectedOrder.shippingAddress.zipCode || 'N/A'}
              </p>
              {selectedOrder.shippingAddress.phoneNumber && (
                <p>Phone: {selectedOrder.shippingAddress.phoneNumber}</p>
              )}
            </div>
          )}
          
          <h4>Products</h4>
          {Array.isArray(selectedOrder.items) && selectedOrder.items.length > 0 ? (
            <table className="order-products-table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>Price</th>
                  <th>Quantity</th>
                  <th>Subtotal</th>
                </tr>
              </thead>
              <tbody>
                {selectedOrder.items.map((item, index) => {
                  if (!item || !item.product) return null;
                  
                  const itemPrice = typeof item.price === 'number' ? item.price : 0;
                  const itemQuantity = typeof item.quantity === 'number' ? item.quantity : 0;
                  
                  return (
                    <tr key={index}>
                      <td>{item.product.name || 'Unknown Product'}</td>
                      <td>{formatPrice(itemPrice)}</td>
                      <td>{itemQuantity}</td>
                      <td>{formatPrice(itemPrice * itemQuantity)}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          ) : (
            <p>No product information available</p>
          )}
          
          <div className="update-status">
            <h4>Update Order Status</h4>
            <div className="status-buttons">
              <button 
                className={`status-btn processing ${selectedOrder.status === 'Processing' ? 'active' : ''}`}
                onClick={() => handleStatusChange(selectedOrder._id, 'Processing')}
                disabled={selectedOrder.status === 'Processing'}
              >
                Processing
              </button>
              <button 
                className={`status-btn shipped ${selectedOrder.status === 'Shipped' ? 'active' : ''}`}
                onClick={() => handleStatusChange(selectedOrder._id, 'Shipped')}
                disabled={selectedOrder.status === 'Shipped'}
              >
                Shipped
              </button>
              <button 
                className={`status-btn delivered ${selectedOrder.status === 'Delivered' ? 'active' : ''}`}
                onClick={() => handleStatusChange(selectedOrder._id, 'Delivered')}
                disabled={selectedOrder.status === 'Delivered'}
              >
                Delivered
              </button>
              <button 
                className={`status-btn cancelled ${selectedOrder.status === 'Cancelled' ? 'active' : ''}`}
                onClick={() => handleStatusChange(selectedOrder._id, 'Cancelled')}
                disabled={selectedOrder.status === 'Cancelled'}
              >
                Cancelled
              </button>
            </div>
          </div>
        </div>
      ) : (
        <div className="orders-table-container">
          <table className="orders-table">
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Customer</th>
                <th>Date</th>
                <th>Total</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.length === 0 ? (
                <tr>
                  <td colSpan="6" className="no-orders">No orders available</td>
                </tr>
              ) : (
                orders.map(order => {
                  if (!order || !order._id) return null;
                  
                  return (
                    <tr key={order._id}>
                      <td className="order-id">{order._id}</td>
                      <td>{order.shippingAddress?.fullName || 'N/A'}</td>
                      <td>{order.createdAt ? formatDate(order.createdAt) : 'Unknown date'}</td>
                      <td className="order-total">{formatPrice(order.totalAmount)}</td>
                      <td className="order-status">
                        <span className={`status-badge ${(order.status || '').toLowerCase()}`}>
                          {order.status || 'Unknown'}
                        </span>
                      </td>
                      <td className="actions-cell">
                        <button 
                          className="view-btn"
                          onClick={() => viewOrderDetails(order)}
                        >
                          View Details
                        </button>
                      </td>
                    </tr>
                  );
                })
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ManageOrders;
