import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { FaEye } from 'react-icons/fa';
import '../../styles/user/MyOrders.css';

const MyOrders = () => {
  const { token, currentUser } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedOrder, setSelectedOrder] = useState(null);

  useEffect(() => {
    fetchOrders();
  }, [token]);

  const fetchOrders = async () => {
    setLoading(true);
    try {
      // For now, let's use mock data since the backend might not be ready
      // In a real application, you would fetch from your API
      setTimeout(() => {
        const mockOrders = [
          {
            _id: 'ORD12345',
            products: [
              { name: 'Organic Apples', quantity: 2, price: 4.99 },
              { name: 'Fresh Milk', quantity: 1, price: 3.49 }
            ],
            totalAmount: 13.47,
            status: 'delivered',
            shippingAddress: {
              address: '123 Main St',
              city: 'Anytown',
              postalCode: '12345',
              country: 'USA'
            },
            paymentMethod: 'credit',
            createdAt: '2025-03-10T10:30:00Z'
          },
          {
            _id: 'ORD12346',
            products: [
              { name: 'Whole Wheat Bread', quantity: 1, price: 3.99 },
              { name: 'Organic Bananas', quantity: 1, price: 2.49 },
              { name: 'Cheddar Cheese', quantity: 1, price: 4.99 }
            ],
            totalAmount: 11.47,
            status: 'shipped',
            shippingAddress: {
              address: '123 Main St',
              city: 'Anytown',
              postalCode: '12345',
              country: 'USA'
            },
            paymentMethod: 'paypal',
            createdAt: '2025-03-15T14:45:00Z'
          },
          {
            _id: 'ORD12347',
            products: [
              { name: 'Chicken Breast', quantity: 2, price: 8.99 },
              { name: 'Mixed Vegetables', quantity: 1, price: 4.49 }
            ],
            totalAmount: 22.47,
            status: 'processing',
            shippingAddress: {
              address: '123 Main St',
              city: 'Anytown',
              postalCode: '12345',
              country: 'USA'
            },
            paymentMethod: 'cash',
            createdAt: '2025-03-20T09:15:00Z'
          }
        ];
        
        setOrders(mockOrders);
        setLoading(false);
      }, 1000);
      
      // Uncomment when backend is ready
      /*
      const response = await fetch('http://localhost:5000/api/orders/my-orders', {
        headers: {
          'x-auth-token': token
        }
      });
      
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Failed to fetch orders');
      }
      
      setOrders(data);
      */
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const openOrderDetails = (order) => {
    setSelectedOrder(order);
  };

  const closeOrderDetails = () => {
    setSelectedOrder(null);
  };

  const getStatusClassName = (status) => {
    switch (status) {
      case 'pending': return 'status-pending';
      case 'processing': return 'status-processing';
      case 'shipped': return 'status-shipped';
      case 'delivered': return 'status-delivered';
      case 'cancelled': return 'status-cancelled';
      default: return '';
    }
  };

  const getPaymentMethodName = (method) => {
    switch (method) {
      case 'cash': return 'Cash on Delivery';
      case 'credit': return 'Credit Card';
      case 'paypal': return 'PayPal';
      default: return method;
    }
  };

  if (loading) {
    return <div className="loading-message">Loading your orders...</div>;
  }

  if (error) {
    return <div className="error-message">Error: {error}</div>;
  }

  if (orders.length === 0) {
    return (
      <div className="no-orders">
        <h3>You haven't placed any orders yet</h3>
        <p>Once you place an order, it will appear here.</p>
      </div>
    );
  }

  return (
    <div className="my-orders">
      <div className="orders-list">
        <table className="orders-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Date</th>
              <th>Total</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id}>
                <td>{order._id}</td>
                <td>{formatDate(order.createdAt)}</td>
                <td className="order-total">${order.totalAmount.toFixed(2)}</td>
                <td>
                  <span className={`order-status ${getStatusClassName(order.status)}`}>
                    {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                  </span>
                </td>
                <td>
                  <button 
                    className="view-order-btn"
                    onClick={() => openOrderDetails(order)}
                  >
                    <FaEye /> View
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Order Details Modal */}
      {selectedOrder && (
        <div className="order-details-modal">
          <div className="order-details-content">
            <div className="order-details-header">
              <h3>Order Details - {selectedOrder._id}</h3>
              <button className="close-modal" onClick={closeOrderDetails}>×</button>
            </div>
            
            <div className="order-details-body">
              <div className="order-details-section">
                <h4>Order Information</h4>
                <div className="order-info-grid">
                  <div>
                    <p><strong>Order Date:</strong> {formatDate(selectedOrder.createdAt)}</p>
                    <p><strong>Status:</strong> 
                      <span className={`order-status ${getStatusClassName(selectedOrder.status)}`}>
                        {selectedOrder.status.charAt(0).toUpperCase() + selectedOrder.status.slice(1)}
                      </span>
                    </p>
                  </div>
                  <div>
                    <p><strong>Payment Method:</strong> {getPaymentMethodName(selectedOrder.paymentMethod)}</p>
                    <p><strong>Total Amount:</strong> ${selectedOrder.totalAmount.toFixed(2)}</p>
                  </div>
                </div>
              </div>
              
              <div className="order-details-section">
                <h4>Shipping Address</h4>
                <p>{selectedOrder.shippingAddress.address}</p>
                <p>{selectedOrder.shippingAddress.city}, {selectedOrder.shippingAddress.postalCode}</p>
                <p>{selectedOrder.shippingAddress.country}</p>
              </div>
              
              <div className="order-items">
                <h4>Order Items</h4>
                <table className="item-table">
                  <thead>
                    <tr>
                      <th>Product</th>
                      <th>Price</th>
                      <th>Quantity</th>
                      <th>Subtotal</th>
                    </tr>
                  </thead>
                  <tbody>
                    {selectedOrder.products.map((item, index) => (
                      <tr key={index}>
                        <td>{item.name}</td>
                        <td>${item.price.toFixed(2)}</td>
                        <td>{item.quantity}</td>
                        <td>${(item.price * item.quantity).toFixed(2)}</td>
                      </tr>
                    ))}
                  </tbody>
                  <tfoot>
                    <tr>
                      <td colSpan="3" className="total-label">Total</td>
                      <td className="total-value">${selectedOrder.totalAmount.toFixed(2)}</td>
                    </tr>
                  </tfoot>
                </table>
              </div>
            </div>
            
            <div className="order-details-footer">
              <button className="close-btn" onClick={closeOrderDetails}>Close</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MyOrders;
